import React, { useState, useEffect } from 'react';

import { withStyles, makeStyles, createMuiTheme, ThemeProvider } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom'
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import Slide from '@material-ui/core/Slide';



import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import CardActionArea from '@material-ui/core/CardActionArea';

import './Styles/slider.css'
import Blocks from './Blocks'
import Footer from '../Components/Footer'

// import NavigationBar from './NavigationBar'
// import BottomPagination from './BottomPagination'

// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";



// Import Swiper styles
import "swiper/swiper.min.css";
import "swiper/components/pagination/pagination.min.css"
import "swiper/components/navigation/navigation.min.css"

import SocialMediaIcons from './SocialMedia';

// import Swiper core and required modules
import SwiperCore, {
    Autoplay, Pagination, Mousewheel, Navigation
} from 'swiper/core';



// install Swiper modules
SwiperCore.use([Autoplay, Mousewheel, Navigation, Pagination]);

const theme = createMuiTheme({
});

const CustomFont = withStyles({
    root: {
        background: 'linear-gradient(to right, #0f2027, #203a43, #2c5364)',
        WebkitBackgroundClip: "text",
        WebkitTextFillColor: "transparent"
    }
})(Typography);



const useStyles = makeStyles({

    bottom: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 3,
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        color: 'white',
        height: 40,
        width: '130px',
        margin: 10,
        marginLeft: 60,
    },
    card: {
        maxWidth: 180,
        objectFit: 'fill',
        '&:hover': {
            maxWidth: 210,
            maxHeight: 140,
            boxShadow: '10px 10px 10px 10px rgba(255, 255, 255, .4)',
            // '&:after': {
            //     content: '"Clorder"',
            //     display: 'block',
            //     height: 100,
            //     marginTop: -100,

            // }
        },
    },
    video: {
        borderRadius: 30,
        // '&:hover': {
        //     Width: 410,
        //     Height: 440,
        //     '&:after': {
        //         Width: 410,
        //         Height: 440,
        //     }
        //  }
    },
    body: {
        height: '90vh',
        borderBottomRightRadius: '40%',
        background: 'linear-gradient(45deg, #93a5cf 0%, #e4efe9 100%)',

    },
});


const title = "Sai Akhil"
// const subtitle = "Projects"
const jobtitle = "Full Stack Developer"

theme.typography.h1 = {
    [theme.breakpoints.up('xs')]: {
        fontSize: '4rem',
    },
    [theme.breakpoints.up('sm')]: {
        fontSize: '5rem',
    },
    [theme.breakpoints.up('md')]: {
        fontSize: '6rem',
    },
    [theme.breakpoints.up('lg')]: {
        fontSize: '7rem',
        fontWeight: 700,
        fontFamily: ['Teko', 'sans-serif'],
    },
    [theme.breakpoints.up('xl')]: {
        fontSize: '8rem',
    },
};
theme.typography.h2 = {
    [theme.breakpoints.up('xs')]: {
        fontSize: '3rem',
    },
    [theme.breakpoints.up('sm')]: {
        fontSize: '4rem',
    },
    [theme.breakpoints.up('md')]: {
        fontSize: '5rem',
    },
    [theme.breakpoints.up('lg')]: {
        fontSize: '5rem',
    },
    [theme.breakpoints.up('xl')]: {
        fontSize: '5rem',
    },
};
theme.typography.h3 = {
    [theme.breakpoints.up('xs')]: {
        fontSize: '0.8rem',
    },
    [theme.breakpoints.up('sm')]: {
        fontSize: '1rem',
    },
    [theme.breakpoints.up('md')]: {
        fontSize: '1.5rem',
    },
    [theme.breakpoints.up('lg')]: {
        fontSize: '2rem',
    },
    [theme.breakpoints.up('xl')]: {
        fontSize: '2rem',
    },
};
theme.typography.subtitle1 = {
    [theme.breakpoints.up('xs')]: {
        fontSize: '0.8rem',
    },
    [theme.breakpoints.up('sm')]: {
        fontSize: '1rem',
    },
    [theme.breakpoints.up('md')]: {
        fontSize: '1.5rem',
    },
    [theme.breakpoints.up('lg')]: {
        fontSize: '1.2rem',
        fontWeight: 800,
        fontFamily: ['Teko', 'sans-serif'],
        textTransform: 'uppercase',
        marginTop: -15
    },
    [theme.breakpoints.up('xl')]: {
        fontSize: '3rem',
    },
};
theme.typography.body1 = {
    [theme.breakpoints.up('xs')]: {
        fontSize: '0.8rem',
    },
    [theme.breakpoints.up('sm')]: {
        fontSize: '1rem',
    },
    [theme.breakpoints.up('md')]: {
        fontSize: '1.5rem',
    },
    [theme.breakpoints.up('lg')]: {
        fontSize: '1rem',
        fontWeight: 800,
        fontFamily: ['Teko', 'sans-serif'],
        marginTop: -10
    },
    [theme.breakpoints.up('xl')]: {
        fontSize: '3rem',
    },
};
theme.typography.body2 = {
    [theme.breakpoints.up('xs')]: {
        fontSize: '0.8rem',
    },
    [theme.breakpoints.up('sm')]: {
        fontSize: '1rem',
    },
    [theme.breakpoints.up('md')]: {
        fontSize: '1.5rem',
    },
    [theme.breakpoints.up('lg')]: {
        fontSize: '0.8rem',
        fontWeight: 1000,
        fontFamily: ['Teko', 'sans-serif'],
    },
    [theme.breakpoints.up('xl')]: {
        fontSize: '3rem',
    },
};


export default function Slider() {
    const classes = useStyles();
    const [slidevalue, setslidevalue] = useState(true);

    const fadein = () => {
        setslidevalue(true);
    }
    const fadeout = () => {
        setslidevalue(false);
    }

    useEffect(() => {
        let id
        id = setInterval(() => {
            fadeout()
        }, 5000);
        return () => clearInterval(id);
    }, [slidevalue]);

    useEffect(() => {
        let a
        if (slidevalue === false) {
            a = setInterval(() => {
                fadein()
            }, 1000);
        }
        return () => clearInterval(a);
    }, [slidevalue]);

    return (
        <>
            <Box className={classes.body}>
                <Swiper style={{ '--swiper-navigation-size': '25px', '--swiper-pagination-color': '#000', '--swiper-navigation-color': '#000' }} speed={1300} pagination={{ "type": "progressbar" }} mousewheel={true} navigation={false}
                    autoplay={{
                        "delay": 10000, "disableOnInteraction": false
                    }}>
                    <SwiperSlide>
                        <Box
                            position="absolute"
                            top="30%"
                            left="30%"
                            zIndex="modal"
                        >
                            <Slide direction="right" in={true} timeout={1000} >
                                <Card className={classes.video}>
                                    <iframe
                                        src="https://www.youtube.com/embed/qAKf-og-siE?autoplay=1&mute=1&playlist=qAKf-og-siE&loop=1"
                                        title="YouTube video player" frameBorder="0"
                                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                        allowFullScreen></iframe>
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="2%"
                            right="10%"
                            zIndex="modal"
                            background='#000'


                        >
