                                <Card className={classes.card}>
                                    <CardMedia
                                        component="img"
                                        alt="IOS"
                                        image="https://res.cloudinary.com/cakhil/image/upload/v1624386400/iamsaiakhil.ga/IOS_regado.png"
                                        title="IOS"
                                    />
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            bottom="20%"
                            left="37%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="right" in={true} timeout={1000} >

                                <Card className={classes.card}>
                                    <CardMedia
                                        component="img"
                                        alt="Android"
                                        image="https://res.cloudinary.com/cakhil/image/upload/v1624386401/iamsaiakhil.ga/Android_flqizw.png"
                                        title="Android"
                                    />
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="40%"
                            left="1%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="right" in={true} timeout={1000} >
                                <Card className={classes.card}>
                                    <CardMedia
                                        component="img"
                                        alt="OFO"
                                        image="https://res.cloudinary.com/cakhil/image/upload/v1624385634/iamsaiakhil.ga/OFO_w2cbhe.png"
                                        title="OFO"
                                    />
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="40%"
                            right="1%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="right" in={true} timeout={1000} >
                                <Card className={classes.card}>
                                    <CardActionArea>
                                        <CardMedia
                                            component="img"
                                            alt="OFOR"
                                            image="https://res.cloudinary.com/cakhil/image/upload/v1624386401/iamsaiakhil.ga/OFOR_qqyyj9.png"
                                            title="OFOR"
                                        />
                                    </CardActionArea>
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="2%"
                            left="10%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="right" in={true} timeout={1000} >
                                <Card className={classes.card}>
                                    <CardActionArea>
                                        <CardMedia
                                            component="img"
                                            alt="Clorder"

                                            image="https://res.cloudinary.com/cakhil/image/upload/v1624253354/iamsaiakhil.ga/clorder-lg_qyndye.png"
                                            title="Clorder"
                                        />
                                    </CardActionArea>
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            bottom="10%"
                            left="0%"
                            zIndex="modal"
                        >
                            <Box display="flex"
                                alignItems="flex-start">
                                <Slide direction="right" in={slidevalue} timeout={1000} >
                                    <CustomFont variant="subtitle1">Clorder</CustomFont>
                                </Slide>
                            </Box>
                            <Box display="flex"
                                alignItems="flex-start">
                                <Slide direction="right" in={slidevalue} timeout={1000} >
                                    <CustomFont variant="subtitle1">Technologies</CustomFont>
                                </Slide>
                            </Box>
                        </Box>
                        <Box
                            position="absolute"
                            bottom="5%"
                            left="0%"
                            zIndex="modal"
                        >
                            {/* <Box display="flex"
                                alignItems="flex-start">
                                <Slide direction="right" in={true} timeout={1000} >
                                    <CustomFont variant="body1"> Software</CustomFont>
                                </Slide>

                            </Box>
                            <Box display="flex"
                                alignItems="flex-start">
                                <Slide direction="right" in={true} timeout={1000} >
                                    <CustomFont variant="body1"> Developer</CustomFont>
                                </Slide>

                            </Box> */}
                            <Box display="flex"
                                alignItems="flex-start">
                                <Slide direction="right" in={slidevalue} timeout={1000} >
                                    <CustomFont variant="body2">Worked on Online food ordering system</CustomFont>
                                </Slide>
                            </Box>
                            <Box display="flex"
                                alignItems="flex-start">
                                <Slide direction="right" in={slidevalue} timeout={1000} >
                                    <CustomFont variant="body2">Worked on Admin, Customer portal, Adminstration portals</CustomFont>
                                </Slide>
                            </Box>
                        </Box>
                    </SwiperSlide>
                    <SwiperSlide>
                        <Box
                            position="absolute"
                            top="30%"
                            left="30%"
                            zIndex="modal"
                        >
                            <Slide direction="up" in={true} timeout={1000} >
                                <Card className={classes.video}>
                                    <iframe
                                        src="https://www.youtube.com/embed/B3mOr_xpXWE?autoplay=1&mute=1&playlist=B3mOr_xpXWE&loop=1"
                                        title="YouTube video player" frameBorder="0"
                                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                        allowFullScreen></iframe>
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="2%"
                            right="10%"
                            zIndex="modal"
                            background='#000'


                        >
                            <Slide direction="up" in={true} timeout={1000} >
                                <Card className={classes.card}>
                                    <CardMedia
                                        component="img"
                                        alt="IOS"
                                        image="https://res.cloudinary.com/cakhil/image/upload/v1624386400/iamsaiakhil.ga/IOS_regado.png"
                                        title="IOS"
                                    />
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            bottom="20%"
                            left="37%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="up" in={true} timeout={1000} >

                                <Card className={classes.card}>
                                    <CardMedia
                                        component="img"
                                        alt="Android"
                                        image="https://res.cloudinary.com/cakhil/image/upload/v1624386401/iamsaiakhil.ga/Android_flqizw.png"
                                        title="Android"
                                    />
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="40%"
                            left="1%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="right" in={true} timeout={1000} >
                                <Card className={classes.card}>
                                    <CardMedia
                                        component="img"
                                        alt="OFO"
                                        image="https://res.cloudinary.com/cakhil/image/upload/v1624385634/iamsaiakhil.ga/OFO_w2cbhe.png"
                                        title="OFO"
                                    />
                                </Card>
                            </Slide>
                        </Box>
                        <Box
                            position="absolute"
                            top="40%"
                            right="1%"
                            zIndex="modal"
                            background='#000'
                        >
                            <Slide direction="up" in={true} timeout={1000} >
